mapper.delete:
/* 회원 탈퇴 */
DELETE
FROM
ingredient_registration
WHERE
ingredient_code = #{ingredientCode};

/*  로그인 이력 삭제 */
DELETE
FROM
ingredient_registration
WHERE
ingredient_code = #{ingredientCode};

mapper.insert:
/* 재료등록 */
INSERT INTO ingredient_registration
(ingredient_code, member_id, ingredient_name, ingredient_category_code, reg_time)
VALUES
(#{ingredientCode}, #{memberId}, #{ingredientName}, #{ingredientCategoryCode}, SYSDATE());
