mapper.delete:
/* 메세지삭제 */
DELETE
FROM
message
WHERE
message_code = #{messageCode}

/*  메세지 이력 삭제 */
DELETE
FROM
message
WHERE
message_code = #{messageCode}

mapper.update:
/* 메세지 정보수정 */
UPDATE message


        message_code = #{messageCode},


        message_details = #{messageDetails}


WHERE
message_code = #{messageCode};

mapper.insert:
/* 메세지 등록 */
INSERT INTO message
(message_code, message_details)
VALUES
(sf_new_message_code(), #{messageDetails});
