mapper.delete:
/* 레시피 삭제 */
DELETE
FROM
recipe_reg
WHERE
recipe_code = #{recipeCode};

/*  레시피 이력 삭제 */
DELETE
FROM
recipe_reg
WHERE
recipe_code = #{recipe_code};

mapper.insert:
/* 레시피등록 */
INSERT INTO recipe_reg
(recipe_code, recipe_name, recipe_category_code, member_id, recipe_cal, recipe_quantity, file_idx, reg_date, recipe_step1, recipe_step2, recipe_step3)
VALUES
(sf_new_recipe_code(), #{recipeName}, #{recipeCategoryCode}, #{memberId}, #{recipeCal}, #{recipeQuantity}, #{fileIdx}, SYSDATE(), #{recipeStep1} ,#{recipeStep2} ,#{recipeStep3});

mapper.select:
/* 레시피 목록 조회 */ SELECT r.recipe_code as recipeCode ,r.recipe_name as recipeName ,r.recipe_category_code as recipeCategoryCode ,r.member_id as memberId ,r.recipe_cal as recipeCal ,r.recipe_quantity as recipeQuantity ,r.reg_date as regDate ,r.update_at as updateAt ,r.file_idx as fileIdx ,r.view_count as viewCount ,r.recipe_use_number as recipeUseNumber ,r.recipe_step1 as recipeStep1 ,r.recipe_step2 as recipeStep2 ,r.recipe_step3 as recipeStep3 FROM recipe_reg AS r INNER JOIN member AS m on r.member_id = m.member_id
${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
order by CAST(SUBSTRING_INDEX(recipe_code, 'recipe_',-1) AS unsigned), r.recipe_code
