mapper.select:
/* 공지사항목록조회 */ SELECT n.notice_board_code, n.member_id, n.notice_board_title, n.notice_board_reg_time FROM notice_board as n INNER JOIN member as m ON n.member_id = m.member_id
${searchKey} = #{searchValue};
order by notice_board_code

mapper.insert:
/* 공지 등록 */
INSERT INTO notice_board
(notice_board_code, member_id, notice_board_title, notice_board_content, notice_board_reg_time)
VALUES
(#{noticeBoardCode}, #{memberId}, #{noticeBoardTitle}, #{noticeBoardContent}, SYSDATE());
