mapper.select:
/* 판매자별 상품조회 */ SELECT m.member_id ,m.member_name ,r.recipe_code ,r.recipe_name ,r.recipe_category_code ,r.member_id ,r.reg_date FROM member as m INNER JOIN recipe_reg as r ON m.member_id = r.member_id
OR ${column} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY r.recipe_code;

mapper.delete:
/* 회원탈퇴 */
DELETE
FROM
    member
WHERE
    member_id = #{memberId}

/*  로그인 이력 삭제 */
DELETE
FROM
    member
WHERE
    member_id = #{memberId}

mapper.insert:
/* 회원가입 */
INSERT INTO member
(member_id, level_num, member_pw, member_name, member_gender, member_birth, member_diet, member_email, member_phone, member_nickname, member_allergy, member_remove, join_date)
VALUES
(#{memberId}, #{levelNum}, #{memberPw}, #{memberName}, #{memberGender}, #{memberBirth}, #{memberDiet}, #{memberEmail}, #{memberPhone}, #{memberNickname}, #{memberAllergy}, #{memberRemove}, CURDATE());

mapper.select:
/* 회원아이디 중복체크 */
SELECT
    if(COUNT(1)=0, 1, 0)
FROM
    member as m
WHERE
    m.member_id  = #{memberId};

/* 회원 등급 조회 */
SELECT
l.level_num 	as levelNum
,l.member_level  	as memberLevel
FROM
level as l;

/* 회원 목록 조회 */ select m.member_id as memberId ,m.member_pw as memberPw ,m.member_name as memberName ,m.level_num as levelNum ,l.member_level as memberLevel ,m.member_email as memberEmail ,m.member_phone as memberPhone ,m.join_date as joinDate from member as m INNER JOIN level as l ON m.level_num = l.level_num
${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
order by m.member_id;
