mapper.update:
/*신고글 수정*/
UPDATE report_board


        report_board_code = #{reportBoardCode}


        ,report_type_code = #{reportTypeCode}


        ,recipe_code = #{recipeCode}


        ,report_info_board_content = #{reportInfoBoardContent}


WHERE
report_board_code = #{reportBoardCode};

mapper.delete:
/* 신고 삭제 */
DELETE
FROM
report_board
WHERE
report_board_code = #{reportBoardCode};

mapper.insert:
/* 신고글 등록 */
INSERT INTO report_board
(report_board_code, member_id, report_type_code, recipe_code, report_state_code, report_info_board_content, report_datetime)
VALUES
(#{reportBoardCode}, #{memberId}, #{reportTypeCode}, #{recipeCode}, #{reportStateCode}, #{reportInfoBoardContent}, SYSDATE());

mapper.select:
/* 신고목록조회 */ SELECT r.report_board_code, r.member_id, r.report_type_code, r.recipe_code, r.report_state_code, r.report_datetime FROM report_board as r INNER JOIN member as m ON r.member_id = m.member_id
${searchKey} = #{searchValue};
order by report_board_code
